mapper.insert:
INSERT INTO
   orderr (uuid, cno, ceo, cname, pcount, omanager) 
      VALUE (uuid_short(), #{cno}, #{ceo}, #{cname}, #{pcount}, #{omanager});

mapper.select:
	 
	 
		SELECT DISTINCT a.ono, a.uuid, a.oday, a.cno, a.ceo, a.cname, a.pcount, a.osuju, a.omanager, a.otext, a.scount, a.sdate, a.sdel,
		
		( SELECT osum FROM cart limit 1) AS osum 
		FROM
		( SELECT @rownum:=@rownum+1 rownum, b.*
		  FROM orderr b,
		( SELECT @rownum:=0 ) AS tmp
		
		
			
			
			
				 
					           
				WHERE oday BETWEEN #{startday} AND #{endday}
				
				
				
				
				
				      	 		
				WHERE oday >= #{startday}
				
				
				
				
				
				      	 		
				WHERE oday <= #{endday}
				
			
			
		
				) AS a 
		INNER JOIN cart AS c ON a.ono = c.ono			
		 
          WHERE rownum > (#{pageNum}-1)*#{amount} AND rownum <= #{pageNum}*#{amount}          
         
         ORDER BY uuid DESC;
