mapper.insert:
INSERT INTO
   orderr (uuid, cno, ceo, cname, pcount, omanager) 
      VALUE (uuid_short(), #{cno}, #{ceo}, #{cname}, #{pcount}, #{omanager});

mapper.select:
		select *
		from (
		select @rownum:=@rownum+1 rownum, b.*
		from orderr b,
		(select @rownum:=0) as tmp
		
			
			
				
				
				 
	       	 	           
	           where oday BETWEEN #{startday} AND #{endday}
	        	
				
				
				
				
      	 	      	 		
      	 		where oday >= #{startday}
      	 	 
				
				
				
				
      	 	      	 		
      	 		where oday <= #{endday}
      	 	 
				
			
		
		order by oday desc
		) as orderr			 
		
 			where rownum > (#{pageNum}-1)*#{amount} and rownum <= #{pageNum}*#{amount}
